import React, { useEffect, useState } from "react";
import "./App.css";

function App() {
  const [todos, setTodos] = useState([]);
  const [input, setInput] = useState("");
  const [joke, setJoke] = useState("");

  useEffect(() => {
    const fetchJoke = async () =>
      await fetch(`http://api.icndb.com/jokes/random`)
        .then((res) => res.json())
        .then((data) => {
          setJoke(data.value.joke);
        });

        fetchJoke();
  }, []); // try to remove [] & see what happens!

  const addTodo = (e) => {
    e.preventDefault();
    setTodos([...todos, input]);
    setInput("");
  };

  return (
    <div className="App">
      {/* Navbar */}
      <div className="flex bg-transparent shadow-2xl h-24 text-6xl text-cyan-400 justify-center p-3">
        To-do
      </div>
      {/* Input Field */}
      <form>
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          type="text"
          className="h-max mt-10 p-2 bg-slate-300 border rounded-lg"
        ></input>
        {/* Button */}
        <button
          type="submit"
          onClick={addTodo}
          className="ml-4 w-12 h-12 bg-blue-600 rounded-full hover:bg-red-700 active:shadow-lg mouse shadow transition ease-in duration-200 focus:outline-none"
        >
          <svg
            viewBox="0 0 20 20"
            enable-background="new 0 0 20 20"
            class="w-6 h-6 inline-block"
          >
            <path
              fill="#FFFFFF"
              d="M16,10c0,0.553-0.048,1-0.601,1H11v4.399C11,15.951,10.553,16,10,16c-0.553,0-1-0.049-1-0.601V11H4.601
                                    C4.049,11,4,10.553,4,10c0-0.553,0.049-1,0.601-1H9V4.601C9,4.048,9.447,4,10,4c0.553,0,1,0.048,1,0.601V9h4.399
                                    C15.952,9,16,9.447,16,10z"
            />
          </svg>
        </button>
      </form>
      {/* List of todos */}
      <h1 className="text-4xl text-white m-10">Your Todos</h1>
      {todos.map((todo) => {
        return (
          <h1 className="text-2xl flex text-white justify-center">{todo}</h1>
        );
      })}

      <button
        type="button"
        class="py-2.5 px-5 mr-2 mb-2 mt-4 text-xl font-medium text-gray-900 bg-slate-300 rounded-full border border-gray-200 hover:bg-gray-100 hover:text-blue-700 focus:z-10 focus:ring-2 focus:ring-blue-700 focus:text-blue-700"
      >
        Make me laugh!
      </button>
      <h3 className="text-xl text-white">{joke}</h3>
    </div>
  );
}

export default App;
