import React, { useState } from "react";
import "./App.css";

function App() {
  const [todos, setTodos] = useState([]);
  const [input, setInput] = useState("");

  const addTodo = (e) => {
    e.preventDefault();
    setTodos([...todos, input]);
    setInput("");
  };

  return (
    <div className="App">
      {/* Navbar */}
      <div className="flex bg-white shadow-2xl h-24 text-6xl text-blue-500 justify-center p-4 hover:text-yellow" id="nav">
        To-do
      </div>
      {/* Input Field */}
      <form>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          className="h-max mt-10 p-2 border rounded-lg"
          id="field"
        ></input>
        {/* Button */}
        <button
          type="submit"
          onClick={addTodo}
          className="ml-4 mt-4 w-12 h-12 bg-blue-600 rounded-full hover:bg-red-700 active:shadow-lg mouse shadow transition ease-in duration-200 focus:outline-none"
          id="button"
        >
          <svg
            viewBox="0 0 20 20"
            enable-background="new 0 0 20 20"
            class="w-6 h-6 inline-block"
          >
            <path
              fill="#FFFFFF"
              d="M16,10c0,0.553-0.048,1-0.601,1H11v4.399C11,15.951,10.553,16,10,16c-0.553,0-1-0.049-1-0.601V11H4.601
                                    C4.049,11,4,10.553,4,10c0-0.553,0.049-1,0.601-1H9V4.601C9,4.048,9.447,4,10,4c0.553,0,1,0.048,1,0.601V9h4.399
                                    C15.952,9,16,9.447,16,10z"
            />
          </svg>
        </button>
      </form>
      {/* List of todos */}
      <h1 className="text-4xl text-blue-500 m-10">Your Todos</h1>
      {todos.map((todo) => {
        return (
          <div class="flex items-center justify-center">
            <div class="bg-white rounded-3xl border shadow-xl p-2 mt-2 w-96">
              <div>
                <h1 className="text-2xl flex text-blue-500 justify-center">
                  {todo}
                </h1>
              </div>
            </div>
          </div>
        );
      })}
    </div>
  );
}

export default App;
